mapper.insert:
INSERT INTO course_resource (
    course_id, teacher_id, title, description, file_name, 
    file_url, file_type, file_size, download_count, status
) VALUES (
    #{courseId}, #{teacherId}, #{title}, #{description}, #{fileName},
    #{fileUrl}, #{fileType}, #{fileSize}, #{downloadCount}, #{status}
)

mapper.update:
UPDATE course_resource SET
    title = #{title},
    description = #{description},
    status = #{status}
WHERE id = #{id}

UPDATE course_resource SET 
    download_count = download_count + 1
WHERE id = #{id}

mapper.select:
SELECT cr.*, u.real_name as teacher_name, c.title as course_name FROM course_resource cr LEFT JOIN user u ON cr.teacher_id = u.id LEFT JOIN course c ON cr.course_id = c.id WHERE cr.status = 'published'
AND (cr.title LIKE CONCAT('%', #{keyword}, '%') OR cr.description LIKE CONCAT('%', #{keyword}, '%') OR cr.file_name LIKE CONCAT('%', #{keyword}, '%'))
AND cr.course_id = #{courseId}
AND cr.file_type = #{fileType}
ORDER BY cr.create_time DESC
