mapper.insert:
INSERT INTO user (
    username, password, email, phone, role, status, avatar, real_name, 
    user_id, college, major, grade, class_name, subject, register_date, 
    last_login, create_time, update_time
) VALUES (
    #{username}, #{password}, #{email}, #{phone}, #{role}, #{status}, #{avatar}, #{realName},
    #{userId}, #{college}, #{major}, #{grade}, #{className}, #{subject}, #{registerDate},
    #{lastLogin}, #{createTime}, #{updateTime}
)

mapper.update:
UPDATE user
username = #{username}, password = #{password}, email = #{email}, phone = #{phone}, role = #{role}, status = #{status}, avatar = #{avatar}, real_name = #{realName}, user_id = #{userId}, college = #{college}, major = #{major}, grade = #{grade}, class_name = #{className}, subject = #{subject}, update_time = #{updateTime},
WHERE id = #{id}

mapper.select:
SELECT * FROM user


        AND (username LIKE CONCAT('%', #{searchQuery}, '%') 
        OR real_name LIKE CONCAT('%', #{searchQuery}, '%')
        OR email LIKE CONCAT('%', #{searchQuery}, '%'))


        AND role = #{roleFilter}


        AND status = #{statusFilter}


ORDER BY create_time DESC
LIMIT #{page}, #{size}

SELECT COUNT(*) FROM user


        AND (username LIKE CONCAT('%', #{searchQuery}, '%') 
        OR real_name LIKE CONCAT('%', #{searchQuery}, '%')
        OR email LIKE CONCAT('%', #{searchQuery}, '%'))


        AND role = #{roleFilter}


        AND status = #{statusFilter}
